function toggleFactor(row){
  row.classList.toggle('open');
}

// Tooltip
const tip=document.getElementById('tooltip');
function showTip(e,text){
  tip.textContent=text;tip.style.display='block';
  moveTip(e);
}
function moveTip(e){
  tip.style.left=(e.clientX+12)+'px';tip.style.top=(e.clientY-8)+'px';
}
function hideTip(){tip.style.display='none'}
document.addEventListener('mousemove',e=>{if(tip.style.display==='block')moveTip(e)});

function switchPanel(p){
  document.querySelectorAll('.nav-item').forEach(n=>n.classList.remove('active'));
  document.querySelectorAll('.panel').forEach(x=>x.classList.remove('active'));
  const navEl=document.querySelector(`[data-panel="${p}"]`);
  const panelEl=document.getElementById(`panel-${p}`);
  if(navEl)navEl.classList.add('active');
  if(panelEl)panelEl.classList.add('active');
}

document.querySelectorAll('.nav-item').forEach(item=>
  item.addEventListener('click',()=>switchPanel(item.dataset.panel))
);

const overlay=document.getElementById('aciOverlay');
document.querySelectorAll('.aci-launch').forEach(btn=>btn.addEventListener('click',()=>{
  overlay.style.display='flex';
  requestAnimationFrame(()=>requestAnimationFrame(()=>overlay.classList.add('visible')));
  document.body.style.overflow='hidden';
  buildHeatmap();
  switchPanel('overview');
}));

function closeOverlay(){
  overlay.classList.remove('visible');
  overlay.classList.remove('sidebar-open');
  setTimeout(()=>{overlay.style.display='none';document.body.style.overflow=''},300);
}
document.getElementById('closeAci').addEventListener('click',closeOverlay);
document.addEventListener('keydown',e=>{if(e.key==='Escape')closeOverlay()});
var menuToggle=document.getElementById('menuToggle');
if(menuToggle){menuToggle.addEventListener('click',function(e){e.stopPropagation();overlay.classList.toggle('sidebar-open');});}
overlay.addEventListener('click',function(e){
  if(window.innerWidth<=768&&overlay.classList.contains('sidebar-open')){
    var sb=document.getElementById('aciSidebar');
    if(sb&&!sb.contains(e.target)&&e.target!==menuToggle)overlay.classList.remove('sidebar-open');
  }
});
